mapper.update:
update qna
qna_update_date = #{updateDate}, qna_delete_yn = #{deleteYn},
qna_answer_writer = #{answerWriter.id}, qna_answer = #{answer}, qna_answer_yn = #{answerYn},
qna_delete_date = #{deleteDate}
where qna_id = #{id}

mapper.select:
select qna_id as id, qna_category_id as "category.id", qna_category_name as "category.name", qna_title as title, user_id as "user.id", user_nickname as "user.nickname", qna_private_yn as privateYn, qna_create_date as createDate, qna_update_date as updateDate, qna_delete_yn as deleteYn, qna_answer_writer as "answerWriter.id", answer_writer_nickname as "answerWriter.nickname", qna_answer_yn as answerYn, product_id as "product.id", product_name as "product.name" from (select q.qna_id, q.qna_category_id, c.qna_category_name, q.qna_title, q.user_id, u1.user_nickname as user_nickname, q.qna_private_yn, q.qna_create_date, q.qna_update_date, q.qna_delete_yn, q.qna_answer_writer, u2.user_nickname as answer_writer_nickname, q.qna_answer_yn, q.product_id, p.product_name, @rownum := @rownum + 1 as row_num from (select @rownum := 0) r, qna q left join qna_category c on q.qna_category_id = c.qna_category_id left join user u1 on q.user_id = u1.user_id left join user u2 on q.qna_answer_writer = u2.user_id left join product p on q.product_id = p.product_id
and q.qna_title like CONCAT('%', #{keyword}, '%')
and q.qna_content like CONCAT('%', #{keyword}, '%')
and q.qna_category_id = #{filt}
and q.qna_answer_yn = #{sort}
and q.qna_delete_yn = 'N'
order by q.qna_id desc ) as qna where row_num between #{begin} and #{end}

select count(*) from qna
and qna_category_id = #{filt}
and qna_answer_yn = #{sort}
and qna_title like CONCAT('%', #{keyword}, '%')
and qna_content like CONCAT('%', #{keyword}, '%')
and qna_delete_yn = 'N'
