mapper.select:
SELECT term_id, term_title, term_content, term_create_date, term_update_date, term_delete_date, term_delete_yn, term_require_yn
FROM term
WHERE term_id = #{id}

mapper.update:
UPDATE user
SET user_point = user_point + #{point}
WHERE user_id = #{id}

UPDATE user
SET user_point = #{point}
WHERE user_id = #{id}

mapper.select:
SELECT 'Folder' AS type, NULL AS itemId, NULL AS imageName, NULL AS create_date, NULL AS update_date, sf.scrap_folder_name AS folderName, sf.scrap_folder_description AS description FROM scrap_folder sf WHERE sf.scrap_folder_id = #{folderId} AND sf.scrap_folder_delete_yn = 'N' AND sf.user_id = #{userId} AND NOT EXISTS ( SELECT 1 FROM product_scrap ps WHERE ps.scrap_folder_id = sf.scrap_folder_id AND ps.product_scrap_delete_yn = 'N' ) AND NOT EXISTS ( SELECT 1 FROM community_scrap cs WHERE cs.scrap_folder_id = sf.scrap_folder_id AND cs.comm_scrap_delete_yn = 'N' )
UNION ALL
-- 상품 스크랩 정보 SELECT 'Product' AS type, ps.product_id AS itemId, pi.product_image_name AS imageName, ps.product_scrap_create_date AS create_date, ps.product_scrap_update_date AS update_date, sf.scrap_folder_name AS folderName, sf.scrap_folder_description AS description FROM scrap_folder sf LEFT JOIN product_scrap ps ON sf.scrap_folder_id = ps.scrap_folder_id LEFT JOIN product p ON ps.product_id = p.product_id LEFT JOIN product_image pi ON ps.product_id = pi.product_id WHERE sf.scrap_folder_id = #{folderId} AND sf.scrap_folder_delete_yn = 'N' AND sf.user_id = #{userId} AND ps.product_scrap_delete_yn = 'N' AND p.product_delete_yn = 'N' AND ps.user_id = #{userId}
UNION ALL
-- 커뮤니티 스크랩 정보 SELECT 'Community' AS type, cs.comm_id AS itemId, c.comm_image_name AS imageName, cs.comm_scrap_create_date AS create_date, cs.comm_scrap_update_date AS update_date, sf.scrap_folder_name AS folderName, sf.scrap_folder_description AS description FROM scrap_folder sf LEFT JOIN community_scrap cs ON sf.scrap_folder_id = cs.scrap_folder_id LEFT JOIN community c ON cs.comm_id = c.comm_id WHERE sf.scrap_folder_id = #{folderId} AND sf.scrap_folder_delete_yn = 'N' AND sf.user_id = #{userId} AND cs.comm_scrap_delete_yn = 'N' AND c.comm_delete_yn = 'N' AND cs.user_id = #{userId}
ORDER BY create_date DESC

mapper.update:
UPDATE product_scrap ps
    INNER JOIN scrap_folder sf ON ps.scrap_folder_id = sf.scrap_folder_id
    INNER JOIN scrap_folder default_sf ON default_sf.scrap_folder_name = '기본 폴더'
    SET ps.scrap_folder_id = default_sf.scrap_folder_id
WHERE sf.scrap_folder_id = #{folderId}
  AND ps.user_id = #{userId}
  AND ps.product_scrap_delete_yn = 'N';

choose.when:
UPDATE product_scrap
SET scrap_folder_id = #{targetFolderId}
WHERE product_id = #{itemId}
AND user_id = #{userId}
AND product_scrap_delete_yn = 'N'

UPDATE community_scrap
SET scrap_folder_id = #{targetFolderId}
WHERE comm_id = #{itemId}
AND user_id = #{userId}
AND comm_scrap_delete_yn = 'N'

mapper.update:
UPDATE scrap_folder
SET scrap_folder_name = #{folderName}, scrap_folder_description = #{folderDescription}
WHERE scrap_folder_id = #{folderId}
  AND user_id = #{userId}
  AND scrap_folder_delete_yn = 'N';

update user set user_complaint_count = #{complaintCount}
